import store from '../store'
import {closeResults, openResults} from './toggle-result'
import searchIndex from './search-index'

const search = (text, output) => {
  if (!text) {
    closeResults(output)
    return
  }

  if (text === store.prevText) {
    return
  }

  store.prevText = text

  const html = {
    class: [],
    method: [],
    member: [],
    function: [],
    variable: [],
    typedef: [],
    external: [],
    file: [],
    test: [],
    testFile: []
  }

  searchIndex.forEach((item, index) => {
    const [src, href, label, kind] = item
    if (src.indexOf(text) > -1) {
      html[kind].push(`
        <li class="search-result-item">
          <a class="search-result-link" href="${href}">
            ${label}
          </a>
        </li>
      `)
    }
  })

  const innerHTML = Object.keys(html)
    .map(kind => {
      const list = html[kind].join('')
      if (!list) {
        return ''
      }
      return `
      <li class="search-separator">
        ${kind}
      </li>
    ${list}`
    })
    .join('')

  if (!innerHTML) {
    closeResults(output)
    return
  }
  openResults(output, innerHTML)
}

export default search

import store from '../store'
import {setHref} from '../url'

const select = (e, node) => {
  const {keyCode} = e
  if (keyCode === 40) {
    // arrow down
    const current = node.children[store.selectedIndex]
    let selected = node.children[store.selectedIndex + 1]
    if (selected && selected.classList.contains('search-separator')) {
      selected = node.children[store.selectedIndex + 2]
      store.selectedIndex++
    }

    if (selected) {
      if (current) {
        current.classList.remove('selected')
      }
      store.selectedIndex++
      selected.classList.add('selected')
    }
  } else if (keyCode === 38) {
    // arrow up
    const current = node.children[store.selectedIndex]
    let selected = node.children[store.selectedIndex - 1]
    if (selected && selected.classList.contains('search-separator')) {
      selected = node.children[store.selectedIndex - 2]
      store.selectedIndex--
    }

    if (selected) {
      if (current) {
        current.classList.remove('selected')
      }
      store.selectedIndex--
      selected.classList.add('selected')
    }
  } else if (keyCode === 13) {
    // enter
    const current = node.children[store.selectedIndex]
    if (current) {
      const link = current.querySelector('a')
      if (link) {
        setHref(link.href)
      }
    }
  } else {
    return
  }
  e.preventDefault()
}

export default select

